# get the hop couter and update the Max-Forwards header if it exists
$avp(s:hop) = sipt_get_hop_counter();
if($avp(s:hop) > 0)
{
	remove_hf("Max-Forwards");
	append_hf("Max-Forwards: $avp(s:hop)\r\n");
}

